#' Bootstrap Item Fit
#'
#' 
#' @param data The data as a data frame.
#' @param mode .
#' @param vars .
#' @param vars1 .
#' @param step .
#' @param bn .
#' @param binfit .
#' @param boutfit .
#' @param inplot .
#' @param outplot .
#' @param angle .
#' @param type .
#' @param adj .
#' @param bn1 .
#' @param outfit .
#' @param infit .
#' @param nco .
#' @param noutfit .
#' @param ninfit .
#' @param width .
#' @param height .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$instructions} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$text} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$item$binfit} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$item$boutfit} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$inplot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$outplot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$instructions1} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$noutfit} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$ninfit} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$outfit} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$infit} \tab \tab \tab \tab \tab a table \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$noutfit$asDF}
#'
#' \code{as.data.frame(results$noutfit)}
#'
#' @export
bfit <- function(
    data,
    mode = "simple",
    vars,
    vars1,
    step = 1,
    bn = 100,
    binfit = TRUE,
    boutfit = FALSE,
    inplot = FALSE,
    outplot = FALSE,
    angle = 0,
    type = "bi",
    adj,
    bn1 = 100,
    outfit = TRUE,
    infit = TRUE,
    nco = FALSE,
    noutfit = FALSE,
    ninfit = FALSE,
    width = 500,
    height = 500) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("bfit requires jmvcore to be installed (restart may be required)")

    if ( ! missing(vars)) vars <- jmvcore::resolveQuo(jmvcore::enquo(vars))
    if ( ! missing(vars1)) vars1 <- jmvcore::resolveQuo(jmvcore::enquo(vars1))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(vars), vars, NULL),
            `if`( ! missing(vars1), vars1, NULL))


    options <- bfitOptions$new(
        mode = mode,
        vars = vars,
        vars1 = vars1,
        step = step,
        bn = bn,
        binfit = binfit,
        boutfit = boutfit,
        inplot = inplot,
        outplot = outplot,
        angle = angle,
        type = type,
        adj = adj,
        bn1 = bn1,
        outfit = outfit,
        infit = infit,
        nco = nco,
        noutfit = noutfit,
        ninfit = ninfit,
        width = width,
        height = height)

    analysis <- bfitClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

