#' LCA with ordinal indicators
#'
#' 
#' @param data .
#' @param vars .
#' @param nc .
#' @param desc .
#' @param fit .
#' @param cp .
#' @param plot .
#' @param width .
#' @param height .
#' @param plot1 .
#' @param width1 .
#' @param height1 .
#' @param angle a number from 0 to 90 defining the angle of the x-axis labels,
#'   where 0 degrees represents completely horizontal labels.
#' @param miss .
#' @param use3step .
#' @param auxVar .
#' @param auxFormula .
#' @param reg .
#' @param regvars .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$instructions} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$text} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$progressBarHTML} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$desc} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$fit} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$cp} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$mem} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$plot1} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$use3step} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$reg} \tab \tab \tab \tab \tab a preformatted \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$desc$asDF}
#'
#' \code{as.data.frame(results$desc)}
#'
#' @export
lcaord <- function(
    data,
    vars,
    nc = 2,
    desc = TRUE,
    fit = FALSE,
    cp = FALSE,
    plot = FALSE,
    width = 500,
    height = 500,
    plot1 = FALSE,
    width1 = 500,
    height1 = 500,
    angle = 0,
    miss = "listwise",
    use3step = FALSE,
    auxVar,
    auxFormula = "depression~peer_support",
    reg = FALSE,
    regvars) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("lcaord requires jmvcore to be installed (restart may be required)")

    if ( ! missing(vars)) vars <- jmvcore::resolveQuo(jmvcore::enquo(vars))
    if ( ! missing(auxVar)) auxVar <- jmvcore::resolveQuo(jmvcore::enquo(auxVar))
    if ( ! missing(regvars)) regvars <- jmvcore::resolveQuo(jmvcore::enquo(regvars))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(vars), vars, NULL),
            `if`( ! missing(auxVar), auxVar, NULL),
            `if`( ! missing(regvars), regvars, NULL))

    for (v in vars) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])

    options <- lcaordOptions$new(
        vars = vars,
        nc = nc,
        desc = desc,
        fit = fit,
        cp = cp,
        plot = plot,
        width = width,
        height = height,
        plot1 = plot1,
        width1 = width1,
        height1 = height1,
        angle = angle,
        miss = miss,
        use3step = use3step,
        auxVar = auxVar,
        auxFormula = auxFormula,
        reg = reg,
        regvars = regvars)

    analysis <- lcaordClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

