#' Latent Class Growth Modeling
#'
#' 
#' @param data .
#' @param vars .
#' @param model .
#' @param nc .
#' @param thr .
#' @param fit .
#' @param est .
#' @param desc .
#' @param cp .
#' @param mc .
#' @param plot1 .
#' @param plot .
#' @param raw .
#' @param width .
#' @param height .
#' @param width1 .
#' @param height1 .
#' @param miss .
#' @param plot2 .
#' @param width2 .
#' @param height2 .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$instructions} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$text} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$progressBarHTML} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$desc} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$fit} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$est} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$cp} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$mc} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$mem} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$plot2} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot1} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$desc$asDF}
#'
#' \code{as.data.frame(results$desc)}
#'
#' @export
lcgm <- function(
    data,
    vars,
    model = list(),
    nc = 2,
    thr = "FALSE",
    fit = FALSE,
    est = FALSE,
    desc = TRUE,
    cp = FALSE,
    mc = TRUE,
    plot1 = FALSE,
    plot = FALSE,
    raw = "FALSE",
    width = 500,
    height = 500,
    width1 = 500,
    height1 = 500,
    miss = "fiml",
    plot2 = FALSE,
    width2 = 500,
    height2 = 500) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("lcgm requires jmvcore to be installed (restart may be required)")

    if ( ! missing(vars)) vars <- jmvcore::resolveQuo(jmvcore::enquo(vars))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(vars), vars, NULL))


    options <- lcgmOptions$new(
        vars = vars,
        model = model,
        nc = nc,
        thr = thr,
        fit = fit,
        est = est,
        desc = desc,
        cp = cp,
        mc = mc,
        plot1 = plot1,
        plot = plot,
        raw = raw,
        width = width,
        height = height,
        width1 = width1,
        height1 = height1,
        miss = miss,
        plot2 = plot2,
        width2 = width2,
        height2 = height2)

    analysis <- lcgmClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

