    "lpaBase",
    inherit = jmvcore::Analysis,
    public = list(
        initialize = function(options, data=NULL, datasetId="", analysisId="", revision=0) {
            super$initialize(
                package = "snowRMM",
                name = "lpa",
                version = c(1,0,0),
                options = options,
                results = lpaResults$new(options=options),
                data = data,
                datasetId = datasetId,
                analysisId = analysisId,
                revision = revision,
                pause = NULL,
                completeWhenFilled = FALSE,
                requiresMissings = FALSE,
                weightsSupport = 'none')
        }))

#' Latent Profile Analysis
#'
#' 
#' @param data .
#' @param vars .
#' @param nc .
#' @param variances .
#' @param covariances .
#' @param overall .
#' @param fit .
#' @param est .
#' @param plot .
#' @param plot1 .
#' @param plot3 .
#' @param plot2 .
#' @param plot4 .
#' @param plot5 .
#' @param angle a number from 0 to 90 defining the angle of the x-axis labels,
#'   where 0 degrees represents completely horizontal labels.
#' @param line .
#' @param use3step .
#' @param auxVar .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$instructions} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$progressBarHTML} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$text} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$overall} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$fit} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$est} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$pc} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$post} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot3} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot2} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot1} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot4} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot5} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$use3step} \tab \tab \tab \tab \tab a preformatted \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$overall$asDF}
#'
#' \code{as.data.frame(results$overall)}
#'
#' @export
lpa <- function(
    data,
    vars,
    nc = 2,
    variances = "equal",
    covariances = "zero",
    overall = TRUE,
    fit = FALSE,
    est = FALSE,
    plot = FALSE,
    plot1 = FALSE,
    plot3 = FALSE,
    plot2 = FALSE,
    plot4 = FALSE,
    plot5 = FALSE,
    angle = 0,
    line = "FALSE",
    use3step = FALSE,
    auxVar) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("lpa requires jmvcore to be installed (restart may be required)")

    if ( ! missing(vars)) vars <- jmvcore::resolveQuo(jmvcore::enquo(vars))
    if ( ! missing(auxVar)) auxVar <- jmvcore::resolveQuo(jmvcore::enquo(auxVar))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(vars), vars, NULL),
            `if`( ! missing(auxVar), auxVar, NULL))


    options <- lpaOptions$new(
        vars = vars,
        nc = nc,
        variances = variances,
        covariances = covariances,
        overall = overall,
        fit = fit,
        est = est,
        plot = plot,
        plot1 = plot1,
        plot3 = plot3,
        plot2 = plot2,
        plot4 = plot4,
        plot5 = plot5,
        angle = angle,
        line = line,
        use3step = use3step,
        auxVar = auxVar)

    analysis <- lpaClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

