raschBase <- if (requireNamespace("jmvcore", quietly=TRUE)) R6::R6Class(
    "raschBase",
    inherit = jmvcore::Analysis,
    public = list(
        initialize = function(options, data=NULL, datasetId="", analysisId="", revision=0) {
            super$initialize(
                package = "snowRMM",
                name = "rasch",
                version = c(1,0,0),
                options = options,
                results = raschResults$new(options=options),
                data = data,
                datasetId = datasetId,
                analysisId = analysisId,
                revision = revision,
                pause = NULL,
                completeWhenFilled = FALSE,
                requiresMissings = FALSE,
                weightsSupport = 'none')
        }))

#' Rasch Model
#'
#' 
#' @param data The data as a data frame.
#' @param vars .
#' @param num .
#' @param step .
#' @param type .
#' @param fit .
#' @param rel .
#' @param imean .
#' @param imeasure .
#' @param ise .
#' @param infit .
#' @param outfit .
#' @param pbis .
#' @param rsm .
#' @param pcm .
#' @param thr .
#' @param wrightmap .
#' @param lrsplit .
#' @param mlsplit .
#' @param waldsplit .
#' @param lr .
#' @param ml .
#' @param ml1 .
#' @param mlsplit1 .
#' @param wald .
#' @param gofplot .
#' @param tlab .
#' @param inplot .
#' @param outplot .
#' @param angle a number from 0 to 90 defining the angle of the x-axis labels,
#'   where 0 degrees represents completely horizontal labels.
#' @param plot1 .
#' @param plot2 .
#' @param plot3 .
#' @param piplot .
#' @param plot4 .
#' @param plot5 .
#' @param res .
#' @param q3 .
#' @param res1 .
#' @param cormatrix .
#' @param width .
#' @param height .
#' @param width1 .
#' @param height1 .
#' @param width2 .
#' @param height2 .
#' @param width3 .
#' @param height3 .
#' @param width4 .
#' @param height4 .
#' @param width5 .
#' @param height5 .
#' @param width6 .
#' @param height6 .
#' @param width7 .
#' @param height7 .
#' @param plot8 .
#' @param num1 .
#' @param width8 .
#' @param height8 .
#' @param nptest .
#' @param matrix .
#' @param npmethod .
#' @param mea1 .
#' @param se1 .
#' @param mea2 .
#' @param se2 .
#' @param plot9 .
#' @param width9 .
#' @param height9 .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$instructions} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$items} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$mf$model} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$mf$rel} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$thr} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$rsm} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$pcm} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$piplot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$gofplot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot1} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot2} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot3} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$inplot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$outplot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$ptotal} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$pmeasure} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$pse} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$pinfit} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$poutfit} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$tm$lr} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$tm$ml} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$tm$ml1} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$tm$wald} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$plot4} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot8} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot5} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$q3} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$cormatrix} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$text} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$text1} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$plot9} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$items$asDF}
#'
#' \code{as.data.frame(results$items)}
#'
#' @export
rasch <- function(
    data,
    vars,
    num = 1,
    step = 1,
    type = "RSM",
    fit = TRUE,
    rel = TRUE,
    imean = TRUE,
    imeasure = TRUE,
    ise = TRUE,
    infit = FALSE,
    outfit = FALSE,
    pbis = FALSE,
    rsm = FALSE,
    pcm = FALSE,
    thr = FALSE,
    wrightmap = TRUE,
    lrsplit = "median",
    mlsplit = "median",
    waldsplit = "median",
    lr = FALSE,
    ml = FALSE,
    ml1 = FALSE,
    mlsplit1 = "median",
    wald = FALSE,
    gofplot = FALSE,
    tlab = "item",
    inplot = FALSE,
    outplot = FALSE,
    angle = 0,
    plot1 = FALSE,
    plot2 = FALSE,
    plot3 = FALSE,
    piplot = FALSE,
    plot4 = FALSE,
    plot5 = FALSE,
    res = "stdr",
    q3 = FALSE,
    res1 = "stdr",
    cormatrix = FALSE,
    width = 500,
    height = 500,
    width1 = 500,
    height1 = 500,
    width2 = 500,
    height2 = 500,
    width3 = 500,
    height3 = 500,
    width4 = 500,
    height4 = 500,
    width5 = 500,
    height5 = 500,
    width6 = 500,
    height6 = 500,
    width7 = 500,
    height7 = 500,
    plot8 = FALSE,
    num1 = 1,
    width8 = 500,
    height8 = 500,
    nptest = FALSE,
    matrix = 500,
    npmethod = "T11",
    mea1,
    se1,
    mea2,
    se2,
    plot9 = FALSE,
    width9 = 500,
    height9 = 500) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("rasch requires jmvcore to be installed (restart may be required)")

    if ( ! missing(vars)) vars <- jmvcore::resolveQuo(jmvcore::enquo(vars))
    if ( ! missing(mea1)) mea1 <- jmvcore::resolveQuo(jmvcore::enquo(mea1))
    if ( ! missing(se1)) se1 <- jmvcore::resolveQuo(jmvcore::enquo(se1))
    if ( ! missing(mea2)) mea2 <- jmvcore::resolveQuo(jmvcore::enquo(mea2))
    if ( ! missing(se2)) se2 <- jmvcore::resolveQuo(jmvcore::enquo(se2))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(vars), vars, NULL),
            `if`( ! missing(mea1), mea1, NULL),
            `if`( ! missing(se1), se1, NULL),
            `if`( ! missing(mea2), mea2, NULL),
            `if`( ! missing(se2), se2, NULL))


    options <- raschOptions$new(
        vars = vars,
        num = num,
        step = step,
        type = type,
        fit = fit,
        rel = rel,
        imean = imean,
        imeasure = imeasure,
        ise = ise,
        infit = infit,
        outfit = outfit,
        pbis = pbis,
        rsm = rsm,
        pcm = pcm,
        thr = thr,
        wrightmap = wrightmap,
        lrsplit = lrsplit,
        mlsplit = mlsplit,
        waldsplit = waldsplit,
        lr = lr,
        ml = ml,
        ml1 = ml1,
        mlsplit1 = mlsplit1,
        wald = wald,
        gofplot = gofplot,
        tlab = tlab,
        inplot = inplot,
        outplot = outplot,
        angle = angle,
        plot1 = plot1,
        plot2 = plot2,
        plot3 = plot3,
        piplot = piplot,
        plot4 = plot4,
        plot5 = plot5,
        res = res,
        q3 = q3,
        res1 = res1,
        cormatrix = cormatrix,
        width = width,
        height = height,
        width1 = width1,
        height1 = height1,
        width2 = width2,
        height2 = height2,
        width3 = width3,
        height3 = height3,
        width4 = width4,
        height4 = height4,
        width5 = width5,
        height5 = height5,
        width6 = width6,
        height6 = height6,
        width7 = width7,
        height7 = height7,
        plot8 = plot8,
        num1 = num1,
        width8 = width8,
        height8 = height8,
        nptest = nptest,
        matrix = matrix,
        npmethod = npmethod,
        mea1 = mea1,
        se1 = se1,
        mea2 = mea2,
        se2 = se2,
        plot9 = plot9,
        width9 = width9,
        height9 = height9)

    analysis <- raschClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

